const transformPairs = () => {
    let pairs = require('../tokenPairs.json')
    let pairs_obj = {
        Pairs: []
    }
    let pairs_transformed = []
    for(let i = 0; i < pairs.length; i++){
        let pair = pairs[i]
        for( let j = 0; j < pair.pairAddresses.length; j++){
            let address_data = pairs[i].pairAddresses[j]
            let pair_data = {
                from: pair.from,
                from_symbol: pair.from_symbol,
                to: pair.to,
                to_symbol: pair.to_symbol,
                factory: address_data.address
            }
            pairs_transformed.push(pair_data)
        }
    }
    fs.writeFileSync('./tokenPairs_final.json', JSON.stringify(pairs_transformed, null, 2) , 'utf-8');
}

const getCrossPairs = async (factory) => {
    let failures = 0
    let pairs = require('./tokenPairsBiSwap.json')
    let crossPairs = []
    for(let i = 0; i < pairs.length; i++){
        failures = 0;
        let pair = pairs[i]
        try{
            let pair_data = {
                from: pair.From,
                from_symbol: pair.From_Symbol,
                to: pair.To,
                to_symbol: pair.To_Symbol,
                pairAddresses: [{ address: pair.Factory, reversed: false}]
            }
            let pairAddress = await factory.methods.getPair(pair.From, pair.To).call();

            var pair_ = new web3.eth.Contract(pairAbi, pairAddress);
            let token0 = await pair_.methods.token0().call();
            let token1 = await pair_.methods.token1().call();

            if(token0 === pair_data.from && token1 === pair_data.to){
                pair_data.pairAddresses.push({address: pairAddress, reversed: false})
            } else {
                console.log(reversed)
                pair_data.pairAddresses.push({address: pairAddress, reversed: false})
            }
            crossPairs.push(pair_data)
        } catch (err) {
            console.log(err)
            failures++
            console.log("Request Failed", failures, "times")
            sleep_time = failures * 10000
            console.log("Sleeping for", sleep_time/1000, "secs")
        }
        if(i%5 == 0){
            console.log("Added", i, " sleeping for 5s")
            sleep(7000)
            fs.writeFileSync('./tokenPairs.json', JSON.stringify(crossPairs, null, 2) , 'utf-8');
        }
    }
}

const getPairs = async (factory) => {

    let n = await factory.methods.allPairsLength().call();
    pairs = []
    for(let i = 0; i < n; i++){
        failures = 0
        try{
            failures = 0
            let pairAddress = await factory.methods.allPairs(i).call();
            var pair = new web3.eth.Contract(pairAbi, pairAddress);
            // let events = await pair.getPastEvents('Swap',{ fromBlock: 17193088 })
    
            let token0 = await pair.methods.token0().call();
            let token1 = await pair.methods.token1().call();
    
            var token0_= new web3.eth.Contract(erc20Abi, token0)
            var token1_= new web3.eth.Contract(erc20Abi, token1)
            let token0_symbol = await token0_.methods.symbol().call()
            let token1_symbol = await token1_.methods.symbol().call()
            pairs.push({"Factory": pairAddress, "From": token0, "From_Symbol": token0_symbol, "To": token1, "To_Symbol": token1_symbol})
        }catch( err ){
            failures++
            if(failures > 5) i++;
            console.log("Request Failed", failures, "times")
            sleep_time = failures * 10000
            console.log("Sleeping for", sleep_time/1000, "secs")
        }
       

        
        sleep(2000)
        if(i%5 == 0){
            console.log("Added", i, " sleeping for 5s")
            sleep(7000)
            fs.writeFileSync('./tokenPairs.json', JSON.stringify(pairs, null, 2) , 'utf-8');
        }
    }
    
}

const sleep = (milliseconds) => {
    var start = new Date().getTime();
    for (var i = 0; i < 1e7; i++) {
      if ((new Date().getTime() - start) > milliseconds){
        break;
      }
    }
  }


